import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import axios from "axios";
import baseURL from "../../../utils/baseURL";


//Login actions

export const loginUserAction = createAsyncThunk(
  "user/login",
  async (payload, { rejectWithValue, getState, dispatch }) => {
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };
    try {
      //htttp address
      const { data } = await axios.post(
        `${baseURL}/users/login`,
        payload,
        config
      );

      // save user into localstorage when logged in
      localStorage.setItem("userInfo", JSON.stringify(data));
      return data;
    } catch (error) {
        if(!error?.response){
            throw error;
        }
        return rejectWithValue(error?.response?.data);
    }
  }
);

//Register actions

export const registerUserAction = createAsyncThunk(
  "user/register",
  async (payload, { rejectWithValue, getState, dispatch }) => {
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };
    try {
      //htttp address
      const { data } = await axios.post(
        `${baseURL}/users/register`,
        payload,
        config
      );
      return data;
    } catch (error) {
        if(!error?.response){
            throw error;
        }
        return rejectWithValue(error?.response?.data);
    }
  }
);


//slices
// get user from local storage to store in our store

const userLoginFromStorage = localStorage.getItem("")
const usersSlices = createSlice({
    name: 'users',
    initialState:{auth: 'False', users:["Bob", "Marley"]},
    extraReducers: (builder)=>{
        // handle pending state for login action
        builder.addCase(loginUserAction.pending, (state, action) => {
            state.userLoading = true;
            state.userAppErr = undefined;
            state.userServerErr = undefined;
        });

        // handle success state
        builder.addCase(loginUserAction.fulfilled, (state,action) => {
            state.userAuth = action?.payload;
            state.userLoading = false;
            state.userAppErr = undefined;
            state.userServerErr = undefined;
        });

        //handle rejected state
        builder.addCase(loginUserAction.rejected, (state, action) =>{
            state.userLoading = false;
            state.userAppErr = action?.payload?.msg;
            state.userServerError = action?.error?.msg;
        });

         // handle pending state for register action
         builder.addCase(registerUserAction.pending, (state, action) => {
          state.userLoading = true;
          state.userAppErr = undefined;
          state.userServerErr = undefined;
      });

      // handle success state
      builder.addCase(registerUserAction.fulfilled, (state,action) => {
          state.userAuth = action?.payload;
          state.userLoading = false;
          state.userAppErr = undefined;
          state.userServerErr = undefined;
      });

      //handle rejected state
      builder.addCase(registerUserAction.rejected, (state, action) =>{
          state.userLoading = false;
          state.userAppErr = action?.payload?.msg;
          state.userServerError = action?.error?.msg;
      });
     },
});

export default usersSlices.reducer;
